INSERT INTO `wp_2_options` VALUES ('164', '_transient_dash_88ae138922fe95674369b1cb3d215a2b', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/09/wordpress-4-3-1/\'>WordPress 4.3.1 Security and Maintenance Release</a> <span class=\"rss-date\">September 15, 2015</span><div class=\"rssSummary\">WordPress 4.3.1 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. This release addresses three issues, including two cross-site scripting vulnerabilities and a potential privilege escalation. WordPress versions 4.3 and earlier are vulnerable to a cross-site scripting vulnerability when processing shortcode tags (CVE-2015-5714). Reported by [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/measure-jetpack-an-independent-project-aimed-at-measuring-jetpacks-performance\'>WPTavern: Measure Jetpack: An Independent Project Aimed at Measuring Jetpack’s Performance</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/10/accelerated-mobile-pages-wordpress/\'>Matt: Accelerated Mobile Pages &amp; WordPress</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/10up-continues-to-make-major-investments-in-wordpress\'>WPTavern: 10up Continues to Make Major Investments in WordPress</a></li></ul></div>', 'no');
