INSERT INTO `wp_2_posts` VALUES ('1', '4', '2015-10-06 03:21:42', '2015-10-06 03:21:42', 'Welcome to <a href=\"http://localhost/wordpress/\">NTQ Demo Sites - Net</a>. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2015-10-06 03:21:42', '2015-10-06 03:21:42', '', '0', 'http://localhost/wordpress/site1/?p=1', '0', 'post', '', '1');
INSERT INTO `wp_2_posts` VALUES ('2', '4', '2015-10-06 03:21:42', '2015-10-06 03:21:42', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/wordpress/site1/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2015-10-06 03:21:42', '2015-10-06 03:21:42', '', '0', 'http://localhost/wordpress/site1/?page_id=2', '0', 'page', '', '0');
