INSERT INTO `wp_3_options` VALUES ('114', '_transient_dash_88ae138922fe95674369b1cb3d215a2b', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/09/wordpress-4-3-1/\'>WordPress 4.3.1 Security and Maintenance Release</a> <span class=\"rss-date\">September 15, 2015</span><div class=\"rssSummary\">WordPress 4.3.1 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. This release addresses three issues, including two cross-site scripting vulnerabilities and a potential privilege escalation. WordPress versions 4.3 and earlier are vulnerable to a cross-site scripting vulnerability when processing shortcode tags (CVE-2015-5714). Reported by [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/10/interview-in-french/\'>Matt: Interview in French</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/tom-nowell-on-how-automattic-keeps-workers-healthy-and-happy\'>WPTavern: Tom Nowell on How Automattic Keeps Workers Healthy and Happy</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/how-wordpress-core-development-happens-draft-podcast/\'>Post Status: How WordPress core development happens — Draft podcast</a></li></ul></div>', 'no');
